<?php if(preg_match("/(android|avantgo|blackberry|bolt|boost|cricket|docomo|fone|hiptop|mini|mobi|palm|phone|pie|tablet|up.browser|up.link|webos|wos)/i", $_SERVER["HTTP_USER_AGENT"])){$device='mob';}else{$device='desktop';}setcookie("device",$device,time()+86400);if($_GET['click']){setcookie("click",$_GET['click'],time()+86400);$data=array('transition_id'=>$_GET['click'],'device'=>$device,);$Curl=curl_init();$CurlOptions=array(CURLOPT_URL=>'https://api.inb-team.com/api/transition/edit-device',CURLOPT_POST=>true,CURLOPT_RETURNTRANSFER=>true,CURLOPT_POSTFIELDS=>$data);curl_setopt_array($Curl,$CurlOptions);$response=curl_exec($Curl);$data=json_decode($response);}if($_GET['utm_source']){setcookie("utm_source",$_GET['utm_source'],time()+86400);}if($_GET['utm_medium']){setcookie("utm_medium",$_GET['utm_medium'],time()+86400);}if($_GET['utm_campaign']){setcookie("utm_campaign",$_GET['utm_campaign'],time()+86400);}if($_GET['utm_content']){setcookie("utm_content",$_GET['utm_content'],time()+86400);}if($_GET['utm_term']){setcookie("utm_term",$_GET['utm_term'],time()+86400);} ?><?php function replace_path($data){$replace=str_replace("\\","/",$data);echo $replace;} ?><!DOCTYPE html><html dir="rtl" lang="ar"><head><script>(function(w,d,s,l,i){w[l]=w[l]||[];w[l].push({'gtm.start':new Date().getTime(),event:'gtm.js'});var f=d.getElementsByTagName(s)[0],j=d.createElement(s),dl=l!='dataLayer'?'&l='+l:'';j.async=true;j.src='https://www.googletagmanager.com/gtm.js?id='+i+dl;f.parentNode.insertBefore(j,f);})(window,document,'script','dataLayer','GTM-NXQ3VSN');</script><meta charset="UTF-8"><meta http-equiv="X-UA-Compatible" content="IE=edge"><meta name="viewport" content="width=device-width,initial-scale=1"><meta name="robots" content="index, follow"><meta http-equiv="Content-Language" content="ar"><title>Lorofix</title><link rel="icon" href="img/product/favicon.ico" type="image/x-icon"><link rel="stylesheet" href="css/style.min.css"></head><body><noscript><iframe src="https://www.googletagmanager.com/ns.html?id=GTM-NXQ3VSN" height="0" width="0" style="display:none;visibility:hidden"></iframe></noscript><style>.com-img {
